xrx:service.xrx:body:
{
if ($session-username != 'guest') then (
    let $updated := update replace $glossary-to-release with yes
    let $updated-released := update insert {current-dateTime()} following $glossary-to-release
    (: let $store := if($glossary-to-release) then atom:PUT(conf:param('xrx-user-atom-base-uri'), concat(xmldb:encode($xrx:user-id), '.xml'), $updated-released)
                  else () :)

    (: send email if document was stored :)
    let $sendmail :=

        release-glossary-email


    return ()
)
else ()
}
