xrx:div.xrx:view:
{


    {
    let $concept := $skosfile/skos:Concept[@rdf:about='#birds'] (: hier mit util:eval XPath mit selektiertem Konzept bauen:)
    return $concept
    }



    var editor = CodeMirror.fromTextArea(document.getElementById('editor'), {{
      mode: 'xml',
      lineNumbers: true
    }});
    editor.setSize("350", "250");

    $('input#submit-content').click(function() {{
      var editorContent = editor.getValue();

      $.ajax({{
        url: "/mom/service/skos-edit?c=birds", // hier den Service angeben
        type: "PUT",
        contentType: "application/xml",
        data: editorContent,     // Editor-Inhalt wird an AJAX übergeben
        dataType: "xml",
      }})
    }});



}
