xrx:view.script:
$(function(){{
  $('#concept-select').on('change', function () {{
    var url = window.location.href;
    var baseUrl = url.split("?")[0];
    console.log(baseUrl);
    var redirectUrl = baseUrl + "?c=" + $(this).val();
    if (redirectUrl) {{
    window.location = redirectUrl;
  }}
  return false;
  }});
}});

