xrx:view.script:
var editor = CodeMirror.fromTextArea(document.getElementById('editor'), {{
  mode: 'xml',
  htmlMode: true,
  lineNumbers: true
}});
editor.setSize("500", "250");
const urlParams = new URLSearchParams(window.location.search);
const concept = urlParams.get('c');

$('input#submit-content').click(function() {{
  var editorContent = editor.getValue();

  $.ajax({{
    url: "/mom/service/skos-edit?c=" + concept, // hier den Service angeben
    type: "PUT",
    contentType: "application/xml",
    data: editorContent,     // Editor-Inhalt wird an AJAX übergeben
    dataType: "xml",
  }})
}});
